package com.lightning.bean.wangzelong;

public class Cargo {
	private int cargo_indent;//订单id
	private float cargo_weight;//货物重量
	private float cargo_size;//货物大小
	private int cargo_type;//货物类型关联type
	private String cargo_explain;//货物备注
	public Cargo() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Cargo(int cargo_indent, float cargo_weight, float cargo_size,
			int cargo_type, String cargo_explain) {
		super();
		this.cargo_indent = cargo_indent;
		this.cargo_weight = cargo_weight;
		this.cargo_size = cargo_size;
		this.cargo_type = cargo_type;
		this.cargo_explain = cargo_explain;
	}
	public Cargo(int cargo_indent) {
		super();
		this.cargo_indent = cargo_indent;
	}
	public Cargo(float cargo_weight, float cargo_size, int cargo_type,
			String cargo_explain) {
		super();
		this.cargo_weight = cargo_weight;
		this.cargo_size = cargo_size;
		this.cargo_type = cargo_type;
		this.cargo_explain = cargo_explain;
	}
	public int getCargo_indent() {
		return cargo_indent;
	}
	public void setCargo_indent(int cargo_indent) {
		this.cargo_indent = cargo_indent;
	}
	public float getCargo_weight() {
		return cargo_weight;
	}
	public void setCargo_weight(float cargo_weight) {
		this.cargo_weight = cargo_weight;
	}
	public float getCargo_size() {
		return cargo_size;
	}
	public void setCargo_size(float cargo_size) {
		this.cargo_size = cargo_size;
	}
	public int getCargo_type() {
		return cargo_type;
	}
	public void setCargo_type(int cargo_type) {
		this.cargo_type = cargo_type;
	}
	public String getCargo_explain() {
		return cargo_explain;
	}
	public void setCargo_explain(String cargo_explain) {
		this.cargo_explain = cargo_explain;
	}
}
