package com.lightning.bean.wangzelong;

public class Type {
	private int type_id;//类型编号（货物）
	private String type;//类别（货物）
	public Type(int type_id, String type) {
		super();
		this.type_id = type_id;
		this.type = type;
	}
	public Type() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Type(int type_id) {
		super();
		this.type_id = type_id;
	}
	
	
	public int getType_id() {
		return type_id;
	}
	public void setType_id(int type_id) {
		this.type_id = type_id;
	}
	public String getType() {
		return type;
	}
	public void setType(String type) {
		this.type = type;
	}
	
}
